import React, { useEffect, useState, useRef } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useNavigate } from 'react-router-dom';
import { handleSuccess } from '../../utils';
import Sidebar from './SSidebar';
import Header from './SHeader';
// import '../../css/StudentCss/StudentDashboardLayout.css';
import '../../css/StudentCss/sDiscussions.css';
import io from 'socket.io-client';
import axios from 'axios';

const SDiscussions = () => {
  const [loggedInUser, setLoggedInUser] = useState(null);
  const navigate = useNavigate();
  const socketRef = useRef();
  
  // State for contacts/messages
  const [contacts, setContacts] = useState([]);
  
  // State for new contact form
  const [showNewContactForm, setShowNewContactForm] = useState(false);
  const [newContactEmail, setNewContactEmail] = useState('');
  const [newContactName, setNewContactName] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const searchTimeoutRef = useRef(null);
  
  // State for selected contact
  const [selectedContactId, setSelectedContactId] = useState(null);
  
  // State for message input
  const [messageInput, setMessageInput] = useState('');

  // Function to search users
  const searchUsers = async (query) => {
    try {
      setIsSearching(true);
      console.log('Starting search with query:', query);
      
      const token = localStorage.getItem('token');
      if (!token) {
        console.error('No token found');
        toast.error('Please login again - No token found');
        navigate('/login');
        return;
      }

      console.log('Making API request with token:', token.substring(0, 10) + '...');
      
      const config = {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        withCredentials: true
      };
      
      console.log('API request config:', config);
      console.log('Full API URL:', `http://localhost:8000/api/student/search-users?query=${encodeURIComponent(query)}`);

      const response = await axios.get(
        `http://localhost:8000/api/student/search-users?query=${encodeURIComponent(query)}`,
        config
      );
      
      console.log('Raw API response:', response);
      
      if (Array.isArray(response.data)) {
        console.log('Valid array response received:', response.data);
        setSearchResults(response.data);
        setShowSuggestions(true);
      } else {
        console.error('Invalid response format:', response.data);
        toast.error('Invalid response format from server');
      }
    } catch (error) {
      console.error('Detailed error object:', error);
      console.error('Error response:', error.response);
      console.error('Error request:', error.request);
      console.error('Error config:', error.config);
      
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error('Error status:', error.response.status);
        console.error('Error data:', error.response.data);
        console.error('Error headers:', error.response.headers);
        
        if (error.response.status === 401) {
          toast.error('Authentication failed - Please login again');
          navigate('/login');
        } else {
          toast.error(`Error: ${error.response.data.message || 'Failed to search users'}`);
        }
      } else if (error.request) {
        // The request was made but no response was received
        console.error('No response received from server');
        toast.error('No response from server - Please check your connection');
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Error setting up request:', error.message);
        toast.error('Error setting up request');
      }
      
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  // Handle input change with debounce
  const handleInputChange = (e, field) => {
    const value = e.target.value;
    if (field === 'name') {
      setNewContactName(value);
    } else {
      setNewContactEmail(value);
    }

    // Clear previous timeout
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    // Set new timeout for search
    if (value.trim().length >= 2) { // Only search if at least 2 characters
      searchTimeoutRef.current = setTimeout(() => {
        searchUsers(value);
      }, 300);
    } else {
      setSearchResults([]);
      setShowSuggestions(false);
    }
  };

  // Handle suggestion selection
  const handleSelectSuggestion = (user) => {
    setNewContactName(user.name);
    setNewContactEmail(user.email);
    setShowSuggestions(false);
    setSearchResults([]);
  };

  // Click outside handler to close suggestions
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.target.closest('.input-container')) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  useEffect(() => {
    const userData = localStorage.getItem('loggedInUser');
    if (userData) {
      const user = JSON.parse(userData);
      setLoggedInUser(user);
      
      // Connect to Socket.IO server
      socketRef.current = io('http://localhost:5000');
      
      // Register user
      socketRef.current.emit('register', {
        userId: user._id,
        name: user.name,
        email: user.email
      });
      
      // Listen for user list updates
      socketRef.current.on('userList', (users) => {
        const filteredUsers = users.filter(u => u.userId !== user._id);
        setContacts(filteredUsers.map(u => ({
          id: u.userId,
          name: u.name,
          email: u.email,
          avatar: '/avatars/default.jpg',
          messages: []
        })));
      });
      
      // Listen for incoming messages
      socketRef.current.on('receiveMessage', ({ from, message, timestamp }) => {
        setContacts(prevContacts => prevContacts.map(contact => {
          if (contact.id === from.userId) {
            return {
              ...contact,
              messages: [...contact.messages, {
                id: Date.now(),
                text: message,
                sender: from.userId,
                timestamp
              }]
            };
          }
          return contact;
        }));
      });
      
      // Listen for sent message confirmation
      socketRef.current.on('messageSent', ({ to, message, timestamp }) => {
        setContacts(prevContacts => prevContacts.map(contact => {
          if (contact.id === to.userId) {
            return {
              ...contact,
              messages: [...contact.messages, {
                id: Date.now(),
                text: message,
                sender: 'me',
                timestamp
              }]
            };
          }
          return contact;
        }));
      });
      
      return () => {
        if (socketRef.current) {
          socketRef.current.disconnect();
        }
      };
    }
  }, []);

  const handleLogout = () => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }
    localStorage.removeItem('token');
    localStorage.removeItem('loggedInUser');
    handleSuccess('User Logged out');
    setTimeout(() => {
      navigate('/login');
    }, 1000);
  };
  
  // Get selected contact
  const selectedContact = contacts.find(contact => contact.id === selectedContactId);
  
  // Handle sending a message
  const handleSendMessage = (e) => {
    e.preventDefault();
    
    if (!messageInput.trim() || !selectedContactId) return;
    
    // Send message through socket
    socketRef.current.emit('sendMessage', {
      to: {
        userId: selectedContactId,
        name: selectedContact.name,
        email: selectedContact.email
      },
      from: {
        userId: loggedInUser._id,
        name: loggedInUser.name,
        email: loggedInUser.email
      },
      message: messageInput
    });
    
    // Clear input
    setMessageInput('');
  };
  
  // Handle adding a new contact
  const handleAddContact = (e) => {
    e.preventDefault();
    
    if (!newContactEmail.trim() || !newContactName.trim()) {
      alert('Please enter both name and email');
      return;
    }
    
    // Reset form
    setNewContactEmail('');
    setNewContactName('');
    setShowSuggestions(false);
    setShowNewContactForm(false);
  };

  return (
    <div className="student-dashboard-container">
      <Sidebar onLogout={handleLogout} />
      <div className="content-area">
        <h1>Welcome, {loggedInUser?.name}</h1>
        <div className="chat-layout">
          {/* Contacts List */}
          <div className="contacts-panel">
            <div className="contacts-header">
              <h2>Messages</h2>
              <button 
                className="add-contact-button"
                onClick={() => setShowNewContactForm(true)}
              >
                + New Chat
              </button>
            </div>
            
            {/* New Contact Form */}
            {showNewContactForm && (
              <div className="new-contact-form">
                <form onSubmit={handleAddContact}>
                  <div className="input-container">
                    <input
                      type="text"
                      placeholder="Search by name..."
                      value={newContactName}
                      onChange={(e) => handleInputChange(e, 'name')}
                      required
                    />
                    {isSearching && <div className="search-loading">Searching...</div>}
                    {showSuggestions && searchResults.length > 0 && (
                      <div className="suggestions-list">
                        {searchResults.map(user => (
                          <div
                            key={user._id}
                            className="suggestion-item"
                            onClick={() => handleSelectSuggestion(user)}
                          >
                            <div className="suggestion-name">{user.name}</div>
                            <div className="suggestion-email">{user.email}</div>
                          </div>
                        ))}
                      </div>
                    )}
                    {showSuggestions && searchResults.length === 0 && !isSearching && (
                      <div className="no-results">No users found</div>
                    )}
                  </div>
                  <div className="input-container">
                    <input
                      type="email"
                      placeholder="Or search by email..."
                      value={newContactEmail}
                      onChange={(e) => handleInputChange(e, 'email')}
                      required
                    />
                    {isSearching && <div className="search-loading">Searching...</div>}
                    {showSuggestions && searchResults.length > 0 && (
                      <div className="suggestions-list">
                        {searchResults.map(user => (
                          <div
                            key={user._id}
                            className="suggestion-item"
                            onClick={() => handleSelectSuggestion(user)}
                          >
                            <div className="suggestion-name">{user.name}</div>
                            <div className="suggestion-email">{user.email}</div>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                  <div className="form-buttons">
                    <button type="submit">Add</button>
                    <button 
                      type="button" 
                      onClick={() => {
                        setShowNewContactForm(false);
                        setShowSuggestions(false);
                        setSearchResults([]);
                        setNewContactName('');
                        setNewContactEmail('');
                      }}
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            )}
            
            {/* Contacts List */}
            <div className="contacts-list">
              {contacts.map(contact => (
                <div 
                  key={contact.id}
                  className={`contact-item ${selectedContactId === contact.id ? 'selected' : ''}`}
                  onClick={() => setSelectedContactId(contact.id)}
                >
                  <div className="contact-avatar">
                    <img src={contact.avatar} alt={contact.name} />
                  </div>
                  <div className="contact-info">
                    <div className="contact-name">{contact.name}</div>
                    <div className="contact-email">{contact.email}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
          
          {/* Chat Area */}
          <div className="chat-area">
            {selectedContact ? (
              <>
                <div className="chat-header">
                  <div className="contact-avatar">
                    <img src={selectedContact.avatar} alt={selectedContact.name} />
                  </div>
                  <div className="contact-info">
                    <div className="contact-name">{selectedContact.name}</div>
                    <div className="contact-email">{selectedContact.email}</div>
                  </div>
                </div>
                
                <div className="messages-container">
                  {selectedContact.messages.length > 0 ? (
                    selectedContact.messages.map(message => (
                      <div 
                        key={message.id}
                        className={`message ${message.sender === 'me' ? 'sent' : 'received'}`}
                      >
                        <div className="message-bubble">
                          {message.text}
                        </div>
                        <div className="message-time">
                          {new Date(message.timestamp).toLocaleTimeString([], {
                            hour: '2-digit',
                            minute: '2-digit'
                          })}
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="no-messages">
                      Start a conversation with {selectedContact.name}
                    </div>
                  )}
                </div>
                
                <form className="message-input-form" onSubmit={handleSendMessage}>
                  <input
                    type="text"
                    placeholder="Type a message..."
                    value={messageInput}
                    onChange={(e) => setMessageInput(e.target.value)}
                  />
                  <button type="submit">Send</button>
                </form>
              </>
            ) : (
              <div className="no-contact-selected">
                <div className="placeholder-message">
                  Select a contact to start chatting
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </div>
  );
};

export default SDiscussions;
